  --emerald-primary: hsl(160, 84%, 39%);
  --amber-primary: hsl(43, 96%, 56%);
  --purple-primary: hsl(262, 83%, 58%);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: 'Inter', sans-serif;
  }
}

@layer components {
  /* Button Components */
  .btn-primary {
    @apply bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-6 rounded-lg transition-all duration-300 inline-flex items-center justify-center shadow-lg hover:shadow-xl transform hover:-translate-y-1 dark:bg-blue-600 dark:hover:bg-blue-700;
  }

  .btn-secondary {
    @apply bg-emerald-600 hover:bg-emerald-700 text-white font-medium py-3 px-6 rounded-lg transition-all duration-300 inline-flex items-center justify-center shadow-lg hover:shadow-xl transform hover:-translate-y-1 dark:bg-emerald-600 dark:hover:bg-emerald-700;
  }

  .btn-outline {
    @apply border-2 border-blue-600 text-blue-600 hover:bg-blue-600 hover:text-white font-medium py-3 px-6 rounded-lg transition-all duration-300 inline-flex items-center justify-center dark:border-blue-400 dark:text-blue-400 dark:hover:bg-blue-400 dark:hover:text-gray-900;
  }

  /* Section Titles */
  .section-title {
    @apply text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-4;
  }

  .section-subtitle {
    @apply text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto;
  }

  /* Cards */
  .glass-card {
    @apply bg-white/80 dark:bg-slate-800/80 backdrop-blur-lg border border-white/20 dark:border-gray-700/20 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300;
  }

  /* Form Inputs */
  .form-input {
    @apply w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white dark:bg-slate-700 text-gray-900 dark:text-gray-100 transition-all duration-300;
  }

  /* Skill Progress Bars */
  .skill-bar {
    @apply w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2 overflow-hidden;
  }

  .skill-progress {
    @apply h-full rounded-full transition-all duration-1000 ease-out;
  }

  /* Social Float Buttons */
  .social-float {
    @apply w-12 h-12 rounded-full flex items-center justify-center shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 hover:scale-110;
  }

  /* Navigation */
  .nav-link {
    @apply text-gray-600 hover:text-blue-600 dark:text-gray-300 dark:hover:text-blue-400 transition-colors duration-200 relative;
  }

  .nav-link.active {
    @apply text-blue-600 dark:text-blue-400;
  }

  .nav-link.active::after {
    content: '';
    @apply absolute bottom-0 left-0 w-full h-0.5 bg-blue-600 dark:bg-blue-400;
  }

  /* Timeline */
  .timeline {
    @apply relative;
  }

  .timeline::before {
    content: '';
    @apply absolute left-4 top-0 w-0.5 h-full bg-blue-500;
  }

  .timeline-item {
    @apply relative pl-12 mb-8;
  }

  .timeline-marker {
    @apply absolute left-0 w-8 h-8 bg-blue-500 rounded-full border-4 border-white dark:border-slate-900;
  }

  /* Project Cards */
  .project-card {
    @apply bg-white dark:bg-slate-800 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 overflow-hidden;
  }

  .project-overlay {
    @apply absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center opacity-0 hover:opacity-100 transition-opacity duration-300;
  }

  /* Tech Tags */
  .tech-tag {
    @apply px-3 py-1 rounded-full text-xs font-medium;
  }

  /* Filter Buttons */
  .filter-btn {
    @apply px-4 py-2 rounded-lg font-medium transition-all duration-300;
  }

  .filter-btn.active {
    @apply bg-blue-600 text-white shadow-lg;
  }

  .filter-btn:not(.active) {
    @apply text-gray-600 dark:text-gray-400 hover:text-blue-600 dark:hover:text-blue-400;
  }

  /* Loading Animation */
  .loading-spinner {
    @apply animate-spin rounded-full h-16 w-16 border-b-4 border-blue-500;
  }
}

@layer utilities {
  /* Custom Animations */
  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-typing {
    animation: typing 3.5s steps(30, end), blink-caret 0.75s step-end infinite;
  }

  .animate-slideInLeft {
    animation: slideInLeft 0.8s ease-out forwards;
  }

  .animate-slideInRight {
    animation: slideInRight 0.8s ease-out forwards;
  }

  .animate-fadeInUp {
    animation: fadeInUp 0.8s ease-out forwards;
  }

  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }
}

/* Keyframes */
@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes typing {
  from { width: 0; }
  to { width: 100%; }
}

@keyframes blink-caret {
  from, to { border-color: transparent; }
  50% { border-color: #3B82F6; }
}

@keyframes slideInLeft {
  0% { transform: translateX(-100px); opacity: 0; }
  100% { transform: translateX(0); opacity: 1; }
}

@keyframes slideInRight {
  0% { transform: translateX(100px); opacity: 0; }
  100% { transform: translateX(0); opacity: 1; }
}

@keyframes fadeInUp {
  0% { transform: translateY(50px); opacity: 0; }
  100% { transform: translateY(0); opacity: 1; }
}

/* Scroll Indicator */
.progress-bar {
  @apply fixed top-0 left-0 h-1 bg-blue-500 z-50 transition-all duration-300;
}

/* Custom Scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  @apply bg-gray-100 dark:bg-gray-800;
}

::-webkit-scrollbar-thumb {
  @apply bg-gray-400 dark:bg-gray-600 rounded-full;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-gray-500 dark:bg-gray-500;
}

