import { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { Rocket, Mail, Download, ChevronDown } from "lucide-react";
import { fadeInUp, float } from "@/lib/animations";
import { useToast } from "@/hooks/use-toast";

const typingTexts = [
  "Problem Solver",
  "Data Science Enthusiast",
  "GIS Analyst",
  "Machine Learning Engineer",
  "Backend Developer",
  "Blockchain Developer"
];

export function HeroSection() {
  const [typingText, setTypingText] = useState("");
  const textIndexRef = useRef(0);
  const charIndexRef = useRef(0);
  const isDeleteingRef = useRef(false);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    const animate = () => {
      const currentText = typingTexts[textIndexRef.current];

      if (!isDeleteingRef.current) {
        // Typing phase
        if (charIndexRef.current < currentText.length) {
          charIndexRef.current += 1;
          setTypingText(currentText.substring(0, charIndexRef.current));
          timeoutRef.current = setTimeout(animate, 100); // Faster typing
        } else {
          // Pause before deleting
          timeoutRef.current = setTimeout(() => {
            isDeleteingRef.current = true;
            animate();
          }, 1500);
        }
      } else {
        // Deleting phase
        if (charIndexRef.current > 0) {
          charIndexRef.current -= 1;
          setTypingText(currentText.substring(0, charIndexRef.current));
          timeoutRef.current = setTimeout(animate, 50); // Faster deleting
        } else {
          // Move to next text
          isDeleteingRef.current = false;
          textIndexRef.current = (textIndexRef.current + 1) % typingTexts.length;
          timeoutRef.current = setTimeout(animate, 500);
        }
      }
    };

    // Start animation after a short delay
    timeoutRef.current = setTimeout(animate, 1000);

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []); // Empty dependency array - runs only once

  const scrollToProjects = () => {
    const element = document.getElementById("projects");
    if (element) {
      const headerOffset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: "smooth"
      });
    }
  };

  const scrollToContact = () => {
    const element = document.getElementById("contact");
    if (element) {
      const headerOffset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: "smooth"
      });
    }
  };

  const downloadResume = () => {
    // Download the actual resume PDF
    const link = document.createElement('a');
    link.href = '/assets/documents/Vansh_Sharma_Resume.pdf';
    link.download = 'Vansh_Sharma_Resume.pdf';
    link.target = '_blank';

    // Check if file exists before attempting download
    fetch(link.href, { method: 'HEAD' })
      .then(response => {
        if (response.ok) {
          link.click();
          toast({
            title: "Resume Downloaded",
            description: "Vansh Sharma's resume has been downloaded successfully.",
          });
        } else {
          // Fallback: show message if resume not found
          toast({
            title: "Resume Unavailable",
            description: "Resume is currently being updated. Please check back soon or contact me directly for the latest version.",
            variant: "destructive",
          });
        }
      })
      .catch(() => {
        // Fallback for network errors
        toast({
          title: "Download Error",
          description: "Resume download temporarily unavailable. Please contact me directly for the latest version.",
          variant: "destructive",
        });
      });
  };

  return (
    <section id="home" className="relative min-h-screen flex items-center justify-center overflow-hidden bg-hero-bg dark:bg-hero-bg">
